CREATE DATABASE tupperwaring;
CREATE TABLE tupperwaring.usuarios(id int(7) auto_increment PRIMARY KEY, password VARCHAR(30) NOT NULL, nombre varchar(30) UNIQUE NOT NULL, apellidos varchar(60), email varchar(30), foto varchar(30),poblacion varchar(40), edad int(3));
CREATE TABLE tupperwaring.tupper(id_tupper int(7) auto_increment PRIMARY KEY, id_usuario int(7), nombre varchar(30) NOT NULL, descripcion varchar(10000) NOT NULL, foto varchar(30) NOT NULL, tipo varchar (15) NOT NULL, vegano boolean, vegetariano boolean, sin_gluten boolean, id_solicitante int(7), solicitado boolean, constraint FK_ID_USUARIO foreign key (id_usuario) references tupperwaring.usuarios(id),constraint FK_ID_SOLICITANTE foreign key (id_solicitante) references tupperwaring.usuarios(id));
CREATE TABLE tupperwaring.mensajes(id_mensaje int(7) auto_increment PRIMARY KEY, id_e int(7),id_r int(7) NOT NULL,id_tupper int(7), fecha date,hora varchar(5),mensaje varchar(1000) NOT NULL,  constraint FK_ID_ENVIADOR foreign key (id_e) references tupperwaring.usuarios(id),constraint FK_ID_RECIBIDOR foreign key (id_r) references tupperwaring.usuarios(id),constraint FK_ID_TUPPER foreign key (id_tupper) references tupperwaring.tupper(id_tupper));
